import Navbar from "../components/NavbarLogin";
import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import AOS from "aos";
import "aos/dist/aos.css";
import { LoginUser, reset, getMe } from "../auth/authSlice";
import Logo from "../images/logo-white.png";
import { ProgressBar } from "react-loader-spinner";

export default function Login() {
  const [isLoaded, setIsLoaded] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { user, isError, isSuccess, isLoading, message } = useSelector(
    (state) => state.auth
  );

  useEffect(() => {
    setTimeout(() => {
      setIsLoaded(false);
    }, 1000);
  }, []);

  // useEffect(() => {
  //   if (user || isSuccess) {
  //     navigate("/learning");
  //   }
  //   dispatch(reset());
  // }, [user, isSuccess, dispatch, navigate]);

  const Auth = async (e) => {
    e.preventDefault();
    dispatch(LoginUser({ email, password }));
  };

  useEffect(() => {
    dispatch(getMe());
  }, [dispatch]);

  useEffect(() => {
    if (isError) {
      navigate("/login");
    }
  }, [isError, navigate]);

  const [passwordVisible, setPasswordVisible] = useState(false);

  useEffect(() => {
    AOS.init();
  }, []);

  const gotoRegister = async () => {
    let path = "/register";
    navigate(path);
  };

  return (
    <>
      {isLoaded ? (
        <div className="flex flex-col items-center justify-center w-full bg-[#205AFC] min-h-screen">
          <img src={Logo} className="w-52" />
          <ProgressBar
            height="70"
            width="200"
            wrapperStyle={{}}
            wrapperClass="progress-bar-wrapper"
            borderColor="#FFFFFF"
            barColor="#daff49"
          />
        </div>
      ) : (
        <div className="min-h-screen">
          <Navbar />
          {/* Home Button */}
          <div className="flex items-center mx-10 mt-5">
            <a
              href="/home"
              className="flex flex-row items-center pointer-events-none group">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                strokeWidth="2"
                stroke="#B7B6B8"
                className="w-12 h-12 border-[3px] border-[#B7B6B8] rounded-full mr-2 hover:border-[#205AFC] pointer-events-auto group-hover:stroke-[#205AFC]">
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  d="M15.75 19.5L8.25 12l7.5-7.5"
                />
              </svg>
              <span className="text-[#B7B6B8] font-semibold text-xl">Home</span>
            </a>
          </div>
          <h1 className="text-black font-bold text-3xl py-10">Log in</h1>
          <form onSubmit={Auth}>
            <div className="flex flex-col justify-center items-center pr-10 gap-2">
              {/* Email */}
              <div className="flex flex-row justify-right items-center gap-3">
                <label>
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    viewBox="0 0 24 24"
                    fill="#B7B6B8"
                    class="w-6 h-6">
                    <path d="M1.5 8.67v8.58a3 3 0 003 3h15a3 3 0 003-3V8.67l-8.928 5.493a3 3 0 01-3.144 0L1.5 8.67z" />
                    <path d="M22.5 6.908V6.75a3 3 0 00-3-3h-15a3 3 0 00-3 3v.158l9.714 5.978a1.5 1.5 0 001.572 0L22.5 6.908z" />
                  </svg>
                </label>
                <input
                  id="email"
                  value={email}
                  className="py-2 [width:400px] font-semibold px-3 rounded-lg border bg-white border-[#B7B6B8]"
                  placeholder="Email"
                  type="text"
                  onChange={(event) => setEmail(event.target.value)}
                  required
                  autoFocus
                />
              </div>

              {/* Password */}
              <div className="flex flex-row justify-right items-center gap-3">
                <label>
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    viewBox="0 0 24 24"
                    fill="#B7B6B8"
                    class="w-6 h-6">
                    <path
                      fill-rule="evenodd"
                      d="M12 1.5a5.25 5.25 0 00-5.25 5.25v3a3 3 0 00-3 3v6.75a3 3 0 003 3h10.5a3 3 0 003-3v-6.75a3 3 0 00-3-3v-3c0-2.9-2.35-5.25-5.25-5.25zm3.75 8.25v-3a3.75 3.75 0 10-7.5 0v3h7.5z"
                      clip-rule="evenodd"
                    />
                  </svg>
                </label>
                <input
                  id="password"
                  type={passwordVisible ? "text" : "password"}
                  placeholder={passwordVisible ? "Password" : "********"}
                  value={password}
                  className="py-2 [width:400px] font-semibold px-3 rounded-lg border bg-white border-[#B7B6B8]"
                  onChange={(event) => setPassword(event.target.value)}
                  required
                  autoComplete="current-password"
                />
              </div>

              {/* Show Password */}
              <label
                htmlFor="toggle-password-visibility"
                className="flex flex-row gap-2 pr-[220px] items-center justify-center cursor-pointer select-none text-gray-700">
                <input
                  type="checkbox"
                  id="toggle-password-visibility"
                  onChange={() => setPasswordVisible(!passwordVisible)}
                  className="bg-white"
                />
                <span className="font-semibold text-[#B7B6B8] text-sm">
                  Show password
                </span>
              </label>
            </div>
            <div className="mt-10">
              <button className="btn-login [width:400px] py-3 rounded-full font-bold normal-case text-lg text-white hover:text-blue-100">
                LOGIN
              </button>
            </div>
          </form>

          {/* Buttons */}
          <div className="pb-10">
            <div className="flex divider justify-center items-center [padding-left:575px] [padding-right:575px] text-[#B7B6B8] font-semibold">
              OR
            </div>
            <button
              onClick={gotoRegister}
              className="btn-register [width:400px] py-3 rounded-full font-bold normal-case text-lg text-white hover:text-green-100">
              CREATE ACCOUNT
            </button>
          </div>
          <a className="text-[#B5BECA] text-sm">
            By signing in to <b>Isyaratlingo</b>, you agree to our{" "}
            <b>Terms and Privacy Policy</b>.
          </a>
        </div>
      )}
    </>
  );
}
