import Navbar from "../../components/NavbarLogin";
import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import ImageSample from "../../images/sample-instruction.png";
import AOS from "aos";
import "aos/dist/aos.css";

export default function LearningInstruction() {
  useEffect(() => {
    AOS.init();
  }, []);

  return (
    <>
      <div className="min-h-screen">
        <Navbar />
        {/* Home Button */}
        <div className="flex items-center mx-10 mt-5">
          <a
            data-tip="Back"
            href="/learning"
            className="flex flex-row items-center pointer-events-none group tooltip tooltip-right">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              strokeWidth="2"
              stroke="#B7B6B8"
              className="w-12 h-12 border-[3px] border-[#B7B6B8] rounded-full mr-2 hover:border-[#205AFC] pointer-events-auto group-hover:stroke-[#205AFC]">
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M15.75 19.5L8.25 12l7.5-7.5"
              />
            </svg>
          </a>
        </div>

        <div className="flex flex-col items-center gap-2">
          <h1
            data-aos="fade-down"
            data-aos-offset="200"
            data-aos-delay="50"
            data-aos-duration="1000"
            className="text-black font-bold text-3xl">
            Instruksi
          </h1>
          <h2

            className="text-black font-semibold text-xl">
            Perkenalan
          </h2>
        <img src={ImageSample} className="w-[500px] h-[250px] rounded-2xl" />
        </div>
      </div>
    </>
  );
}
