import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend,
  } from "chart.js";
  import { Bar } from "react-chartjs-2";
  import { useState, useEffect } from "react";
  import axios from "axios";
  
  ChartJS.register(
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend
  );
  
  export default function ChartsUser() {
    const [userCounts, setUserCounts] = useState({}); // Store user counts by month
  
    useEffect(() => {
      // Fetch user data from your API
      const fetchUsers = async () => {
        try {
          const response = await axios.get("http://localhost:5000/user");
          const users = response.data;
  
          // Initialize an object to store user counts by month
          const counts = {};
  
          // Iterate through user data
          users.forEach((user) => {
            const createdDate = new Date(user.created_at);
  
            // Extract month and year as a string (e.g., "August 2023")
            const monthYear = createdDate.toLocaleDateString("en-US", {
              year: "numeric",
              month: "long",
            });
  
            // Check if the month-year exists in the counts object
            if (!counts[monthYear]) {
              counts[monthYear] = 1;
            } else {
              counts[monthYear]++;
            }
          });
  
          setUserCounts(counts);
        } catch (error) {
          console.error("Error fetching user data:", error);
        }
      };
  
      fetchUsers();
    }, []);
  
    const options = {
      responsive: true,
      plugins: {
        legend: {
          display: false, // Hide legend or customize as needed
        },
        title: {
          display: true,
          text: "User Registration by Month",
        },
      },
      scales: {
        x: {
          title: {
            display: true,
            text: "Month",
          },
        },
        y: {
          beginAtZero: true,
          title: {
            display: true,
            text: "Number of Users",
          },
        },
      },
    };
  
    const labels = Object.keys(userCounts);
    const data = {
      labels,
      datasets: [
        {
          label: "Users",
          data: Object.values(userCounts),
          backgroundColor: "rgba(53, 162, 235, 0.5)",
        },
      ],
    };
  
    return <Bar options={options} data={data} />;
  }
