import { useState, useEffect } from "react";
import Sidebar from "../../../components/SidebarLearning";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { getMe } from "../../auth/authSlice";
import { useDispatch, useSelector } from "react-redux";

export default function AdminDashboard() {
  const [users, setUsers] = useState([]);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { isError } = useSelector((state) => state.auth);

  useEffect(() => {
    getUsers();
  }, []);

  const getUsers = async () => {
    const response = await axios.get(`http://localhost:5000/user`);
    setUsers(response.data);
  };

  const deleteUser = async (uuid) => {
    await axios.delete(`http://localhost:5000/user/${uuid}`);
    getUsers();
  };

  useEffect(() => {
    dispatch(getMe());
  }, [dispatch]);

  useEffect(() => {
    if (isError) {
      navigate("/login");
    }
  }, [isError, navigate]);

  const formatTimestamp = (timestamp) => {
    const options = {
      year: "numeric",
      month: "2-digit",
      day: "2-digit",
      timeZone: "Asia/Jakarta",
    };
    return new Intl.DateTimeFormat("en-US", options).format(
      new Date(timestamp)
    );
  };

  return (
    <div className="flex flex-col">
      <Sidebar />
      <div className="flex items-center justify-center min-w-max overflow-x-auto mt-7">
        <table className="table w-1/2 bg-[#007bff] rounded-lg text-white">
          <thead className="text-white text-center">
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Age</th>
              <th>Email</th>
              <th>Created At</th>
              <th>Update At</th>
              <th></th>
              <th></th>
            </tr>
          </thead>
          <tbody className="text-center">
            {users
              .filter(
                (user) =>
                  user.id ||
                  user.name ||
                  user.age ||
                  user.email ||
                  user.createdAt ||
                  user.updatedAt
              )
              .map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.name}</td>
                  <td>{user.age}</td>
                  <td>{user.email}</td>
                  <td>{formatTimestamp(user.createdAt)}</td>
                  <td>{formatTimestamp(user.updatedAt)}</td>
                  <td>
                    <Link
                      to={`/admin/dashboard/user/edit/${user.uuid}`}
                      className="bg-green-500 p-2 rounded-lg text-white">
                      Edit
                    </Link>
                  </td>
                  <td>
                    <button
                      onClick={() => {
                        if (
                          window.confirm(
                            "Are you sure you wish to delete this item?"
                          )
                        )
                          deleteUser(user.uuid);
                      }}
                      className="bg-red-700 p-2 rounded-lg text-white">
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
