import React, { useState, useEffect } from "react";
import Sidebar from "../../components/SidebarLearning";
import RightDetail from "../../components/RightDetail";
import axios from "axios";

export default function Dictionary() {
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [words, setWords] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Fetch words based on the selected category
    setIsLoading(true);
    axios.get(`/api/words?category=${selectedCategory}`).then((response) => {
      setWords(response.data);
      setIsLoading(false);
    });
  }, [selectedCategory]);

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar setSelectedCategory={setSelectedCategory} />
      <RightDetail>
        <div className="p-6">
          <h1 className="text-3xl font-medium mb-6">Dictionary</h1>
          {isLoading ? (
            <p>Loading...</p>
          ) : (
            <>
              {words.length === 0 ? (
                <p>The vocabs are currently unavailable.</p>
              ) : (
                <ul>
                  {words.map((word) => (
                    <li key={word.id}>{word.word}</li>
                  ))}
                </ul>
              )}
            </>
          )}
        </div>
      </RightDetail>
    </div>
  );
}
