import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";
import { Listbox, Transition } from "@headlessui/react";
import Sidebar from "../../../components/SidebarLearning";

export default function editShop() {
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");
  const { uuid } = useParams();
  const navigate = useNavigate();

  const updateItem = async (e) => {
    e.preventDefault();
    await axios.patch(`http://localhost:5000/shop/${uuid}`, {
      name: name,
      price: price,
      description: description,
    });
    let path = "/admin/dashboard/shop";
    navigate(path);
    window.alert("Item Updated Successfully");
  };

  useEffect(() => {
    getItemById();
  }, []);

  const getItemById = async () => {
    const response = await axios.get(`http://localhost:5000/shop/${uuid}`);
    setName(response.data.name);
    setPrice(response.data.price);
    setDescription(response.data.description);
  };

  return (
    <div className="flex flex-col">
      <Sidebar />
      <div className="flex flex-col items-center  justify-center min-w-max mt-7">
        <div className="py-5">
          <span className="text-xl font-bold">Edit Vocab</span>
        </div>
        <form onSubmit={updateItem}>
          <div>
            <section className="grid grid-cols-2 gap-6">
              {/* Name */}

              <div>
                <label className="label font-bold">Name</label>
                <input
                  className="py-2 [width:300px] font-semibold px-3 rounded-lg border bg-white border-[#B7B6B8]"
                  type="name"
                  placeholder="Name"
                  value={name}
                  required
                  onChange={(e) => setName(e.target.value)}
                />
              </div>

              {/* Price */}

              <div>
                <label className="label font-bold">Price</label>
                <input
                  className="py-2 [width:300px] font-semibold px-3 rounded-lg border bg-white border-[#B7B6B8]"
                  type="text"
                  placeholder="Price"
                  value={price}
                  required
                  onChange={(e) => setPrice(e.target.value)}
                />
              </div>

              {/* Description */}

              <div>
                <label className="label font-bold">Description</label>
                <input
                  className="py-2 [width:300px] font-semibold px-3 rounded-lg border bg-white border-[#B7B6B8]"
                  type="text"
                  placeholder="Description"
                  value={description}
                  required
                  onChange={(e) => setDescription(e.target.value)}
                />
              </div>

              {/* Button */}
              <div className="col-span-2">
                <button className="btn-register [width:300px] py-3 rounded-full font-bold normal-case text-lg text-white hover:text-green-100">
                  Update Vocab
                </button>
              </div>
            </section>
          </div>
        </form>
      </div>
    </div>
  );
}
