import Navbar from "../components/NavbarLogin";
import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import AOS from "aos";
import "aos/dist/aos.css";
import Logo from "../"

export default function Register() {
  const [isLoaded, setIsLoaded] = useState(true);
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmpassword, setConfirmPassword] = useState("");
  const [msg, setMsg] = useState("");
  const history = useNavigate();
  const navigate = useNavigate();

  useEffect(() => {
    setTimeout(() => {
      setIsLoaded(false);
    }, 1000);
  }, []);

  const Register = async (e) => {
    e.preventDefault();
    try {
      if (password === confirmpassword) {
        await axios.post("http://localhost:5000/user", {
          name: name,
          age: age,
          email: email,
          password: password,
          confirmpassword: confirmpassword,
        });
        let path = "/";
        navigate(path);
        window.alert("Register Successfully");
        history.push("/login");
      } else {
        setMsg("Password do not match !");
      }
    } catch (error) {
      if (error.response) {
        setMsg(error.response.data.msg);
      }
    }
  };

  useEffect(() => {
    AOS.init();
  }, []);

  const gotoLogin = async () => {
    let path = "/login";
    navigate(path);
  };
  return (
    <>
      {isLoaded ? (
        <div className="flex flex-col items-center justify-center w-full bg-[#205AFC] min-h-screen">
          <img src={Logo} className="w-52" />
          <ProgressBar
            height="70"
            width="200"
            wrapperStyle={{}}
            wrapperClass="progress-bar-wrapper"
            borderColor="#FFFFFF"
            barColor="#daff49"
          />
        </div>
      ) : (
        <div className="min-h-screen">
          <Navbar />
          {/* Home Button */}
          <div className="flex items-center mx-10 mt-5">
            <a
              href="/home"
              className="flex flex-row items-center pointer-events-none group">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                strokeWidth="2"
                stroke="#B7B6B8"
                className="w-12 h-12 border-[3px] border-[#B7B6B8] rounded-full mr-2 hover:border-[#205AFC] pointer-events-auto group-hover:stroke-[#205AFC]">
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  d="M15.75 19.5L8.25 12l7.5-7.5"
                />
              </svg>
              <span className="text-[#B7B6B8] font-semibold text-xl">Home</span>
            </a>
          </div>
          <h1 className="text-black font-bold text-3xl py-10">
            Create your account
          </h1>
          <form onSubmit={Register}>
            <div className="flex flex-col justify-center items-center gap-2">
              {/* Name */}
              <div className="flex flex-row justify-right items-center gap-3">
                <input
                  id="name"
                  value={name}
                  className="py-2 [width:400px] font-semibold px-3 rounded-lg border bg-white border-[#B7B6B8]"
                  placeholder="Name"
                  type="text"
                  onChange={(event) => setName(event.target.value)}
                  required
                  autoFocus
                />
              </div>

              {/* Age */}
              <div className="flex flex-row justify-right items-center gap-3">
                <input
                  id="age"
                  value={age}
                  className="py-2 [width:400px] font-semibold px-3 rounded-lg border bg-white border-[#B7B6B8]"
                  placeholder="Age"
                  type="text"
                  onChange={(event) => setAge(event.target.value)}
                  required
                  autoFocus
                />
              </div>

              {/* Email */}
              <div className="flex flex-row justify-right items-center gap-3">
                <input
                  id="email"
                  value={email}
                  className="py-2 [width:400px] font-semibold px-3 rounded-lg border bg-white border-[#B7B6B8]"
                  placeholder="Email"
                  type="text"
                  onChange={(event) => setEmail(event.target.value)}
                  required
                  autoFocus
                />
              </div>

              {/* Password */}
              <div className="flex flex-row justify-right items-center gap-3">
                <input
                  id="password"
                  type="password"
                  placeholder="Password"
                  value={password}
                  className="py-2 [width:400px] font-semibold px-3 rounded-lg border bg-white border-[#B7B6B8]"
                  onChange={(event) => setPassword(event.target.value)}
                  required
                  autoComplete="current-password"
                />
              </div>

              {/* Confirm Passowrd */}
              <div className="flex flex-row justify-right items-center gap-3">
                <input
                  id="confirmpassword"
                  type="password"
                  placeholder="Confirm Password"
                  value={confirmpassword}
                  className="py-2 [width:400px] font-semibold px-3 rounded-lg border bg-white border-[#B7B6B8]"
                  onChange={(event) => setConfirmPassword(event.target.value)}
                  required
                  autoComplete="current-password"
                />
              </div>
            </div>
            <div className="mt-10">
              <button className="btn-register [width:400px] py-3 rounded-full font-bold normal-case text-lg text-white hover:text-green-100">
                CREATE ACCOUNT
              </button>
            </div>
          </form>

          {/* Buttons */}
          <div className="pb-10">
            <div className="flex divider justify-center items-center [padding-left:575px] [padding-right:575px] text-[#B7B6B8] font-semibold">
              OR
            </div>
            <button
              onClick={gotoLogin}
              className="btn-login [width:400px] py-3 rounded-full font-bold normal-case text-lg text-white hover:text-blue-100">
              LOGIN
            </button>
          </div>
          <a className="text-[#B5BECA] text-sm">
            By registering to <b>Isyaratlingo</b>, you agree to our{" "}
            <b>Terms and Privacy Policy</b>.
          </a>
        </div>
      )}
    </>
  );
}
