import Navbar from "../components/NavbarLogin";
import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";
import AOS from "aos";
import "aos/dist/aos.css";

export default function ResetPassword() {
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmpassword, setConfirmPassword] = useState("");
  const [msg, setMsg] = useState("");
  const history = useNavigate();
  const navigate = useNavigate();

  const Reset = async (e) => {
    e.preventDefault();
    try {
      if (password === confirmpassword) {
        await axios.post("http://localhost:5000/user", {
          name: name,
          age: age,
          email: email,
          password: password,
          confirmpassword: confirmpassword,
        });
        let path = "/login";
        navigate(path);
        window.alert("Register Successfully");
        history.push("/login");
      } else {
        setMsg("Password do not match !");
      }
    } catch (error) {
      if (error.response) {
        setMsg(error.response.data.msg);
      }
    }
  };

  useEffect(() => {
    AOS.init();
  }, []);

  const gotoLogin = async () => {
    let path = "/login";
    navigate(path);
  };
  return (
    <>
      <div className="min-h-screen">
        <Navbar />
        <h1
          data-aos="fade-down"
          data-aos-offset="200"
          data-aos-delay="50"
          data-aos-duration="1000"
          className="text-black font-bold text-3xl py-10">
          Reset Password
        </h1>
        <form
          data-aos="zoom-in"
          data-aos-offset="200"
          data-aos-delay="50"
          data-aos-duration="1000"
          onSubmit={Reset}>
          <div className="flex flex-col justify-center items-center gap-2">
            
            {/* Password */}
            <div className="flex flex-row justify-right items-center gap-3">
              <input
                id="password"
                type="password"
                placeholder="Password"
                value={password}
                className="py-2 [width:400px] font-semibold px-3 rounded-lg border bg-white border-[#B7B6B8]"
                onChange={(event) => setPassword(event.target.value)}
                required
                autoComplete="current-password"
              />
            </div>

            {/* Confirm Passowrd */}
            <div className="flex flex-row justify-right items-center gap-3">
              <input
                id="confirmpassword"
                type="password"
                placeholder="Confirm Password"
                value={confirmpassword}
                className="py-2 [width:400px] font-semibold px-3 rounded-lg border bg-white border-[#B7B6B8]"
                onChange={(event) => setConfirmPassword(event.target.value)}
                required
                autoComplete="current-password"
              />
            </div>
          </div>
          <div className="mt-10">
            <button className="btn-register [width:400px] py-3 rounded-full font-bold normal-case text-lg text-white hover:text-green-100">
              CREATE ACCOUNT
            </button>
          </div>
        </form>

        {/* Buttons */}
        <div
          data-aos="zoom-in"
          data-aos-offset="150"
          data-aos-delay="50"
          data-aos-duration="1000"
          className="pb-10">
          <div className="flex divider justify-center items-center [padding-left:575px] [padding-right:575px] text-[#B7B6B8] font-semibold">
            OR
          </div>
          <button
            onClick={gotoLogin}
            className="btn-login [width:400px] py-3 rounded-full font-bold normal-case text-lg text-white hover:text-blue-100">
            LOGIN
          </button>
        </div>
        <a
          data-aos="zoom-in"
          data-aos-offset="50"
          data-aos-delay="50"
          data-aos-duration="1000"
          className="text-[#B5BECA] text-sm">
          By registering to <b>Isyaratlingo</b>, you agree to our{" "}
          <b>Terms and Privacy Policy</b>.
        </a>
      </div>
    </>
  );
}
